generated.css:
body {
  margin: 0;
}

.header {
  background: #dc1471;
  height: 80px;
  width: 100%;
  position: absolute;
  top: 0;
}
.header ul {
  list-style: none;
  display: flex;
  place-content: center;
  margin: 0;
}
.header ul li {
  margin: 0 30px;
  font-size: 25px;
  color: #f2f2f2;
  cursor: pointer;
}

.navbar {
  position: relative;
  top: 50%;
  bottom: 50%;
  transform: translate(-50%, -50%);
  left: 50%;
}

.form {
  display: none;
  margin-top: 100px;
  text-align: center;
}
.form input.input-text {
  height: 30px;
  font-size: 25px;
  outline: none;
  color: chocolate;
}
.form button {
  background: transparent;
  border: 1px solid #dc1471;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 5px;
}

.display-img {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 20px;
  padding-top: 30px;
  margin-top: 100px;
  text-align: center;
}

.fav {
  background: transparent;
  border: 1px solid #dc1471;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 5px;
}

.user-files {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
}
.user-files img {
  margin: 10px;
}

.fav-btn {
  text-align: center;
}

.upload-form {
  display: none;
}
.upload-form button {
  background: transparent;
  border: 1px solid #dc1471;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 5px;
}

.upload-div {
  margin-top: 120px;
  text-align: center;
}
.upload-div .dnd {
  display: none;
}
.upload-div .dnd div {
  border: 1px solid #dc1471;
  padding: 20px;
  margin: 10px;
  display: inline-block;
  border-radius: 5px;
  cursor: pointer;
}

form#upload {
  margin-top: 100px;
}

#upload, #messages {
  display: none;
}

legend {
  font-weight: bold;
  color: #333;
}

#filedrag {
  display: none;
  font-weight: bold;
  text-align: center;
  padding: 1em 0;
  margin: 1em 0;
  color: #555;
  border: 2px dashed #555;
  border-radius: 7px;
  cursor: default;
}

#filedrag.hover {
  color: #f00;
  border-color: #f00;
  border-style: solid;
  box-shadow: inset 0 3px 4px #888;
}

#messages {
  margin: 1em 0;
  border-top: 1px solid #999;
}
#messages img {
  padding: 10px;
  width: 31%;
  margin-left: 7px;
}
